
public class swap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       int n1=10;
       int n2=20;
       System.out.println("n1= "+n1+" n2= "+n2);
       int temp;
       temp=n1;
       n1=n2;
       n2=temp;
       System.out.println("Swap nos. are n1= "+n1+" n2= "+n2);
	}

}
